<template>
  <div class="card" style="width: 15rem;">
  <img src="data:image/jpeg;base64," alt="...">
  <div class="card-body">
    <h5 class="card-title">Name: {{stname }}</h5>
    <p class="card-text">Martyr of the nation and its hero</p>
    <a href="#" class="btn btn-primary">City: {{ stcity }}</a>
  </div>
  <template>{{stid=$route.params.id}}</template>
</div>
</template>

<script setup>
import {ref,onMounted} from 'vue'
   let stid= ref('');
   let stname= ref('');
   let stcity= ref('');
   onMounted(()=>{
    loadeData();
   })
 
   async function loadeData() {
       let response =  await fetch(`http://localhost:5000/students/${stid.value}`)
       let result= await response.json();
       console.log(result);
       stname.value = result.name;
       stcity.value = result.city;
    }

</script>

<style scoped>
.card {
    margin-top:70px !important;
    width:300px !important;
    height:500px !important;
}

</style>